EXPORT_SHOW="export | sort | grep -v SHLVL | grep -v _= | grep -v OLDPWD"
exec_test 'export =' 1
exec_test "export 1TEST= \n$ENV_SHOW" 2
exec_test "export TEST \n$EXPORT_SHOW" 2
exec_test "export \"\"=\"\" \n$ENV_SHOW" 2
