exec_test '> lol echo test lol\n cat lol' 2
exec_test '>lol echo > test>lol>test>>lol>test mdr >lol test >test\n cat test' 2
exec_test 'cat < ls' 1
exec_test 'cat < ls > ls' 1

# SYNTAX ERROR
exec_test '\n\n test' 3
exec_test '| test' 1
exec_test 'echo > <' 1
